#!/usr/bin/env node

const path = require('path');
const fs = require('fs');

const axeCoreCliExist = fs.existsSync(
  path.join(process.cwd(), 'node_modules', '@axe-core', 'cli')
);

if (axeCoreCliExist) {
  // eslint-disable-next-line no-console
  console.log(
    'Please make sure to uninstall @axe-core/cli in favor of @axe-devtools/cli'
  );
}
